
public class Elseifcondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=60;

int b=90;

int c=30;

if(a>b && a>c)
{
	System.out.println("A is greater");
}

else if(b>a && b>c)
{
	
	System.out.println("B is greater");
}

else if(c>a && c>b)
{
	
	System.out.println("C is greater");
}
		
	}

}
