
public class Switchcondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name="vignan";
		
		switch (name) {
		case "Abhi":
			
			System.out.println("Name is Abhi");
			break;
		case "vignan":
			
			System.out.println("Name is Vignan");
			break;
		case "narendra"	:
			
			System.out.println("Name is narendra");
			break;
			
			
		default:
			
			System.out.println("Doesn't match any switch case");
			break;
		}
		
	}

}
